mapper.select:
select *
from tb_repair
where  ssd_sn=#{ssd_sn}
and pcba_sn=#{pcba_sn}

mapper.update:
update tb_repair
set result=#{result},
operation=#{operation},
part=#{part},
operator=#{operator},
repair_date=now(),
retest_area=#{retest_area},
element=#{element},
dc=#{dc},
others=#{others}
where ssd_sn=#{ssd_sn}
and pcba_sn=#{pcba_sn}
and error_area=#{error_area}

update tb_repair
set outbound_time=now(),
outbound_receiver=#{outbound_receiver}
where ssd_sn=#{ssd_sn}
and pcba_sn=#{pcba_sn}
